import Layout from '../../components/MyLayout';
import axios from 'axios';

const Post = (props:any) => (
  <Layout>
    <h1>{props.post.data.title}</h1>
    <p>{props.post.data.body}</p>
    <p>{props.post.data.comments && props.post.data.comments.body}</p>
    <form onSubmit={handleSubmit}>
            <div>
            <input
              type="text"
              value={title}
              placeholder="Etner the title"
              onChange={handleTitleChange}
            />
            </div>
            {(error && title.trim() === '') && <div>Tht title is empty</div>}
            <textarea 
              value={body} 
              placeholder="Enter the post" 
              onChange={handleBodyChange} 
            />
            {(error && body.trim() === '') && <div>Tht post field is empty</div>}
            <button type="submit">Create</button>
          </form>
  </Layout>
);

Post.getInitialProps = async function(context: any) {
  const { id } = context.query;
  const res = await axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`);

  return { post: res.data };
};

export default Post;
