import Layout from '../../components/MyLayout';
import axios from 'axios';

const Post = (props:any) => (
  <Layout>
    <h1>{props.show.title}</h1>
    <p>{props.show.body.replace(/<[/]?[pb]>/g, '')}</p>
  </Layout>
);

Post.getInitialProps = async function(context: any) {
  const { id } = context.query;
  const res = axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`);
  const show = await res.json();

  console.log(`Fetched show: ${res}`);

  return { show };
};

export default Post;
