import axios, { AxiosError } from 'axios';

const apiClient = axios.create({
  baseURL: 'https://simple-blog-api.crew.red',
  responseType: 'json',
  headers: {
    'Content-Type': 'application/json'
  }
});

export const createPost = async (newPost: Post) => {
  try {
    const response = await apiClient.post<Post>('/posts', newPost);
    const post = response.data;
    return post;
  } catch (err) {
    if (err && err.response) {
      const axiosError = err as AxiosError;
      return axiosError.response.data;
    }

    throw err;
  }
};

export const createComment = async (newPComment: Post) => {
  try {
    const response = await apiClient.post<Post>('/posts', newPComment);
    const post = response.data;
    return post;
  } catch (err) {
    if (err && err.response) {
      const axiosError = err as AxiosError;
      return axiosError.response.data;
    }

    throw err;
  }
};
