import Layout from '../components/MyLayout';
import { FC } from 'react';
import Link from 'next/link';
import styled from 'styled-components'
import axios from 'axios';

interface Props  {
  posts: Post[];
}

const Heading = styled.h1`
  width: 200px;
  margin: 0 auto;
`

const PostList = styled.ul`
  list-style-type: none;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;

`

const Post = styled.li`
`

const Index = (props: Props) =>  {
  return (
    <Layout>
    <Heading>Latest posts</Heading>
    <PostList>
      {props.posts && props.posts.map((post:Post, i:number) => (i === 500) ?(
        <Post key={post.id}>
          <img className="img" src="https://images.unsplash.com/photo-1501504905252-473c47e087f8?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=2000&fit=max&ixid=eyJhcHBfaWQiOjExNzczfQ" alt="post photo"/>
          <br/>
          <Link href="/posts/[id]`" as={`/posts/${post.id}`}>
            <a>{props.posts[i].data.title}</a>
          </Link>
        </Post>
      ):<></>)}
    </PostList> 
  </Layout>
  );
}

Index.getInitialProps = async function() {
  const res = await axios.get('https://simple-blog-api.crew.red/posts/');

  return {
    posts: res.data
  };
};

export default Index;
