import { ADD_POST, FETCH_POSTS, FETCH_COMMENTS, ADD_COMMENT } from '../constants/action-types';
import { getPosts, getComments } from '../api/client';

export function addPost () {
  debugger;
  return {
    type: ADD_POST,
  }
}

export const fetchPosts = () => async (dispatch: any) => {
  const posts = await getPosts();
  return dispatch({ type: FETCH_POSTS, posts });
};

export const fetchComments = () => async (dispatch: any) => {
  const posts = await getComments();
  return dispatch({ type: FETCH_COMMENTS, posts });
}
