import { useState, ChangeEvent, FormEvent } from 'react';
import Layout from '../../components/MyLayout';
import axios from 'axios';

const Post = (props:any) => {
  const [comment, setComment] = useState('');

  const handleInputChange = (e: ChangeEvent<HTMLInputElement>) => {
    setComment(e.target.value);
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (comment.trim() !== '') {
      await axios
      .post('https://simple-blog-api.crew.red/posts/${id}?_embed=comments', { comments: { body: comment }});
      setComment('');
    } 
  }  

  return (
    <Layout>
    <h1>{props.post.data.title}</h1>
    <p>{props.post.data.body}</p>
    <p>{props.post.data.comments && props.post.data.comments.body}</p>
    <form onSubmit={handleSubmit}>
            <input
              type="text"
              value={comment}
              placeholder="Etner the comment"
              onChange={handleInputChange}
            />
            <button type="submit">Add</button>
          </form>
  </Layout>
  );
}
Post.getInitialProps = async function(context: any) {
  const { id } = context.query;
  const postFromServer = await axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`);
  const commentsFromServer = await axios.get('https://simple-blog-api.crew.red/comments')

  return { post: postFromServer.data };
};

export default Post;
