import Layout from '../../components/MyLayout';
import axios  from 'axios';
import { useState, ChangeEvent, FormEvent } from 'react';
import styled from 'styled-components'

const Heading = styled.h1`
  width: 250px;
  margin: 30px auto 40px;
`
const SubmitButton = styled.button`
  width: 70px;
  text-align: center;
  margin: 0 auto;
  cursor: pointer;
`
const NewPost = () => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [error, setError] = useState(false);
  const NewPostForm = styled.form`
  width: 250px;
  margin: 30px auto 50px;
  text-align: center;

  input {
    width: 100%;
    padding: 5px;
    border: 1px solid black;
    border-radius: 2px;
  }

  textarea {
    background: ${props => error ? "palevioletred" : "white"};
    color: ${props => props.primary ? "white" : "palevioletred"};
    width: 100%;
    padding: 5px;
    margin: 20px auto 20px;
    border: 1px solid black;
    border-radius: 2px;
  }
`
  const handleTitleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setError(false);
    setTitle(e.target.value);
  };

  const handleBodyChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
    setError(false);
    setBody(e.currentTarget.value);
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    if (title.trim() !== '' &&  body.trim() !== '') {
      await axios
        .post('https://simple-blog-api.crew.red/posts', { title, body });
      setTitle('');
      setBody('');
    } else {
      setError(true);
    }
  }  
    return(
      <Layout>
        <Heading>Create new post</Heading>
          <NewPostForm onSubmit={handleSubmit}>
            <input
              primary
              type="text"
              value={title}
              name="title"
              placeholder="Enter the title"
              onChange={handleTitleChange}
            />
            {(error && title.trim() === '') && <div>Tht title is empty</div>}
            <textarea
              rows={5}
              value={body} 
              name="body"
              placeholder="Enter the post" 
              onChange={handleBodyChange} 
            />
            {(error && body.trim() === '') && <div>Tht post field is empty</div>}
            <SubmitButton type="submit">Create</SubmitButton>
          </NewPostForm>
      </Layout>
    );
  };

export default NewPost;
