import Layout from '../components/MyLayout';
import { FC } from 'react';
import Link from 'next/link';
import styled from 'styled-components'
import axios from 'axios';

interface Props  {
  posts: Post[];
}

const Heading = styled.h1`
  width: 200px;
  margin: 30px auto 50px;
`

const PostList = styled.ul`
  list-style-type: none;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 50px;
  padding: 0;

  .post-photo {
    width: 100%;
  }

  .title {
    text-decoration: none;
    font-weight: bold;
    font-size: 20px;
    color: #000;

    &:active {
      color: blue;
    }
  }

  .content {
    font-family: serif;
    color: grey;
    margin-top: 5px;
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr 1fr;
    gap: 30px;
  }

  @media (max-width: 500px) {
    grid-template-columns: 1fr;
    gap: 15px;
  }

`

const Index = (props: Props) =>  {
  return (
    <Layout>
    <Heading>Latest posts</Heading>
    <PostList>
      {props.posts && props.posts.map((post:Post, i:number) => (
        <li key={post.id}>
          <img className="post-photo" src="https://images.unsplash.com/photo-1501504905252-473c47e087f8?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=2000&fit=max&ixid=eyJhcHBfaWQiOjExNzczfQ" alt="post photo"/>
          <br/>
          <Link href="/posts/[id]`" as={`/posts/${post.id}`}>
            <a className="title">{props.posts[i].bodytitle}</a>
          </Link>
          <p className="content">{props.posts[i].data.body}</p>
        </li>
      ))}
    </PostList> 
  </Layout>
  );
}

Index.getInitialProps = async function() {
  const res = await axios.get('https://simple-blog-api.crew.red/posts/');

  return {
    posts: res.data
  };
};

export default Index;
