import Layout from '../../components/MyLayout';
import axios  from 'axios';
import { useState, ChangeEvent, FormEvent } from 'react';

const NewPost = () => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [error, setError] = useState(false);

  const handleTitleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setTitle(e.target.value);
  };

  const handleBodyChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
    setBody(e.currentTarget.value);
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    if (title.trim().length > 0 && body.trim().length > 0) {
      await axios
        .post('https://simple-blog-api.crew.red/posts', { data:{ title, body }});
      setTitle('');
      setBody('');
    } else {
      setError(true);
    }
  }  
    return(
      <Layout>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              value={title}
              placeholder="title"
              onChange={handleTitleChange}
            />
            {(error && title.trim() === '') && <div>Tht title is empty?</div>}
            <textarea 
              value={body} 
              placeholder="post" 
              onChange={handleBodyChange} 
            />
            <button type="submit">Create</button>
          </form>
      </Layout>
    );
  };

// Post.getInitialProps = async function(context: any) {
//   const { id } = context.query;
//   const res = await axios.post(`https://simple-blog-api.crew.red/posts/`);

//   return { post: res.data };
// };

export default NewPost;
