import { ADD_POST, FETCH_POSTS, FETCH_COMMENTS, ADD_COMMENT } from '../constants/action-types';
import { combineReducers } from 'redux';

type ActionPost = {
  type: string,
  post: Post,
  posts: Post[]
}

const postStore = (state = { posts: [] }, action: ActionPost) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        posts: state.posts.push(action.post)
      }
    case FETCH_POSTS:
      co({
        ...state,
        posts: action.posts
      });
      return {
        ...state,
        posts: action.posts
      }
    default:
      return state
  }
};

type ActionComment = {
  type: string,
  comments: Comment[]
}

const commentStore = (state = { comments: [] }, action: ActionComment) => {
  switch (action.type) {
    case ADD_COMMENT:
      return {
        ...state,
      }
    case FETCH_COMMENTS:
      return {
        ...state,
        comments: action.comments
      }
    default:
      return state
  }
};

export default combineReducers({
  postStore,
  commentStore
});
