import Layout from '../../components/MyLayout';
import axios from 'axios';

const Post = (props:any) => (
  <Layout>
    <h1>Hi</h1>
    <h1>{props.post}</h1>
    {/* <p>{props.post.body.replace(/<[/]?[pb]>/g, '')}</p> */} */}
  </Layout>
);

Post.getInitialProps = async function(context: any) {
  const { id } = context.query;
  const res = await axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`);

  console.log(`Fetched show: ${(await res).data.title}`);

  return { post: res.data };
};

export default Post;
