import { ADD_POST, FETCH_POSTS, FETCH_POST, FETCH_COMMENTS, ADD_COMMENT } from '../constants/action-types';
import { getPosts, getPost, getComments, createPost, createComment } from '../api/client';

export const fetchPosts = () => async (dispatch: any) => {
  const posts = await getPosts();
  return dispatch({ type: FETCH_POSTS, posts });
};

export const fetchPost = (postId) => async (dispatch: any) => {
  const post = await getPost(postId);
  return dispatch({ type: FETCH_POST, post });
};

export const fetchComments = () => async (dispatch: any) => {
  const comments = await getComments();
  return dispatch({ type: FETCH_COMMENTS, comments });
}

export const addComment = (newComment) => async (dispatch: any) => {
  const comment = await createComment(newComment);
  return dispatch({ type: ADD_COMMENT, comment });
};
