import axios from 'axios';

const apiClient = axios.create({
  baseURL: 'https://simple-blog-api.crew.red',
  responseType: 'json',
  headers: {
    'Content-Type': 'application/json'
  }
});

export const createPost = async (newPost: any) => {
  try {
    const response = await apiClient.post<any>('/posts', newPost);
    const post = response.data;
    return post;
  } catch (err) {
    if (err && err.response) {
      console.log("couldn't fetch data");
    }

    throw err;
  }
};

export const createComment = async (newComment: Comment) => {
  try {
    const response = await apiClient.post<Comment>('/comments', newComment);
    const comment = response.data;
    return comment;
  } catch (err) {
    if (err && err.response) {
      console.log("couldn't fetch data");
    }

    throw err;
  }
};
